let lastScrollTop=0;const cloud=document.querySelectorAll(".cloud");document.addEventListener("scroll",()=>{let l=window.pageYOffset||document.documentElement.scrollTop;l>lastScrollTop?cloud.forEach(l=>{l.classList.contains("cloud_less")?l.style.transform="translateX(30px)":l.style.transform="translateX(100px)"}):cloud.forEach(l=>{l.classList.contains("cloud_less")?l.style.transform="translateX(-30px)":l.style.transform="translateX(-100px)"}),lastScrollTop=l<=0?0:l});